arguments.argument:
/// Update Colors.

// Blue:
if (color_blue > 0)
{
    color_blue -= 20;
}

// Green:
if (color_green > 0 && color_blue < 170)
{
    color_green -= 20;
}

// Red:
if (color_red > 0 && color_green < 170)
{
    color_red -= 20;
}
else if (color_red == 0)
{
    instance_destroy();
}
