arguments.argument:
/// Finish.
if (trigger)
{
    if (!finish)
    {
        if (!audio_is_playing(SFX._jingle_gameover))
        {
            finish = true;
        }
    }
    else
    {
        if (!instance_exists(obj_fade_game_restart))
        {
            instance_create(0, 0, obj_fade_game_restart);
        }
    }
}
