arguments.argument:
/// Change & select options.
if (instance_exists(obj_fade_to)) exit;

// Change options:
if (keyboard_check_pressed(global.key_up))
{
    if (option_selected != 0)
    {
        option_selected -= 1;
    }
    else
    {
        option_selected = 2;
    }
    audio_play(SFX._general_switch, SFX.sfx_volume, 1, 0, 0);
}
if (keyboard_check_pressed(global.key_down))
{
    if (option_selected != 2)
    {
        option_selected += 1;
    }
    else
    {
        option_selected = 0;
    }
    audio_play(SFX._general_switch, SFX.sfx_volume, 1, 0, 0);
}

// Select options:
if (keyboard_check_pressed(global.key_action))
{
    // Continue:
    if (option_selected == 0)
    {
        instance_activate_all();
        sound_resume_all();
        if (background_exists(game_snapshot))
        {
            background_delete(game_snapshot);
        }
        instance_destroy();
    }

    // Restart:
    if (option_selected == 1)
    {
        // Reset checkpoint variables.
        global.checkpoint_x = -1;
        global.checkpoint_y = -1;
        global.checkpoint_t =  0;

        // Create fade effect.
        room_fade_to(room);
    }

    // Exit:
    if (option_selected == 2)
    {
        // Reload savestates.
        save_reload_all();

        // Reset checkpoint variables.
        global.checkpoint_x = -1;
        global.checkpoint_y = -1;
        global.checkpoint_t =  0;

        // Create fade effect.
        room_fade_to(rm_title);
    }
}
