arguments.argument:
/// Effect size and alpha.
if (fade_size < SCREEN_WIDTH)
{
    fade_size += 16;
}
else
{
    if (fade_alpha > 0)
    {
        fade_alpha -= 0.05;
    }
    else
    {
        obj_continue_controller.alarm[1]  = 30;
        obj_continue_post.sign_post_state = 1;
        instance_destroy();
    }
}
