arguments.argument:
/// Variables.
option_selected    = 0;  // Option we're currently highlighting.
option_description = ""; // Description of a highlighted option.
map_delay          = 15; // Prevents an immediate map of another input after mapping an initial one.
map_source         = 0;  // Which input source we're mapping (0 = None, 1 = Keyboard, 2 = Gamepad).
map_order          = 1;  // Holds the order at which inputs are mapped.
map_string         = ""; // The name of the input we're mapping.
volume_delay       = 5;
option_ended       = false;
image_timer        = 200;
image_speed        = 0;
