arguments.argument:
/// Animation + Position.
if (activated && !finished)
{
    if (bulb_loop > 0)
    {
        post_frame += 2;
    }
    else
    {
        image_index = 0;
        image_speed = 0;
        alarm[0]    = 1;
    }

    if (post_frame >= image_number - 1)
    {
        bulb_loop -= 1;
        post_frame = 0;
    }
}
