arguments.argument:
/// Motion (dropped).

// Apply motion:
if (motion)
{
    // Decrease lifespan:
    lifespan = max(lifespan - 1, 0);

    // Horizontal movement and collision:
    if (place_meeting(x + x_speed, y, par_solid))
    {
        x_speed *= -0.25;
    }
    else
    {
        x += x_speed;
    }

    // Vertical movement and collision:
    y_speed += y_accel;
    if (place_meeting(x, y + y_speed, par_solid) || (y_speed >= 0 && place_meeting(x, y + y_speed, par_platform) && !place_meeting(x, y, par_platform)))
    {
        y_speed *= -0.75;
    }
    else
    {
        y += y_speed;
    }

    // Destroy if the lifespan has run out or if outside the view.
    if (lifespan <= 0 || !in_view(id))
    {
        instance_destroy();
    }
}

/// Trigger magnetization.

// Exit if in motion.
if (motion) exit;

// Find nearest player:
var player;
player = instance_nearest(x, y, par_player);
if (player != noone)
{
    // Magnetize the ring by checking if the player has an electric shield.
    if (player.shield_index == SHIELD_ELECTRIC)
    {
        if (distance_to_object(player) < 64)
        {
            magnetized = true;
            instance_destroy();
        }
    }
}

/// Animation speed.
if (!motion)
{
    image_index = STG.object_time div 120;
}
else
{
    if (lifespan > 120)
    {
        image_index = STG.object_time div 80;
    }
    if (lifespan < 120)
    {
        image_index = STG.object_time div 200;
    }
}
