arguments.argument:
/// Toggle handle type.

// Check if another 'hang' exists on either side:
if (!place_meeting(x - 1, y, par_hang) && place_meeting(x + 1, y, par_hang))
{
    left   = true;
    right  = false;
    middle = false;
}
else if (place_meeting(x - 1, y, par_hang) && !place_meeting(x + 1, y, par_hang))
{
    left   = false;
    right  = true;
    middle = false;
}
else if (!place_meeting(x - 1, y, par_hang) && !place_meeting(x + 1, y, par_hang))
{
    left   = false;
    right  = false;
    middle = true;
}
