arguments.argument:
/// Destroy and give the player some fresh air.
if (bubble_pop && other.shield_index != SHIELD_BUBBLE && other.state != STATE_DEATH)
{
    other.drown_timer = 1800;
    other.state       = STATE_BREATH;
    audio_play(SFX._player_air, SFX.sfx_volume, 1, 0, 0);
    instance_destroy();
}
