arguments.argument:
/// Clean-up.

// CPU input queues:
if (global.character[1] > 0)
{
    ds_queue_destroy(cpu_ip_up);
    ds_queue_destroy(cpu_ip_down);
    ds_queue_destroy(cpu_ip_a_press);
}

// Trail:
if (global.use_trail)
{
    ds_list_destroy(vertex_x[1]);
    ds_list_destroy(vertex_x[2]);
    ds_list_destroy(vertex_y[1]);
    ds_list_destroy(vertex_y[2]);
}
