arguments.argument:
/// Movement & Player position.

// Set previous positions.
previous_x = x;
previous_y = y;

// Set Horizontal/Vertical speed.
if (x_spd < xd_speed)
{
    xd_speed -= 0.025;
}
else if (x_spd > xd_speed)
{
    xd_speed += 0.025;
}

if (y_spd < yd_speed)
{
    yd_speed -= 0.025;
}
else if (y_spd > yd_speed)
{
    yd_speed += 0.025;
}

x_speed += xd_speed;
y_speed += yd_speed;

x = floor(relative_x + x_speed);
y = floor(relative_y + y_speed);
