arguments.argument:
/// Boot sequence.
if (s8e_step == 0)
{
    if (s8e_alpha < 1)
    {
        s8e_alpha += 0.045;
    }
    else
    {
        s8e_alpha = 1;
        s8e_step = 1;
        alarm[0] = 60;
    }
}
if (s8e_step == 2)
{
    if (s8e_alpha > 0)
    {
        s8e_alpha -= 0.045;
    }
    else
    {
        s8e_alpha = -1;
        s8e_step = 3;
        alarm[1] = 30;
    }
}
if (s8e_step == 4)
{
    if (s8e_alpha < 1)
    {
        s8e_alpha += 0.045;
    }
    else
    {
        s8e_alpha = 1;
        s8e_step = 1;
        alarm[2] = 60;
    }
}

if (s8e_done)
{
    s8e_galpha += 0.045;
}
